import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './ApiKeyScreen.css';

function ApiKeyScreen({ story, onStart }) {
  const [apiKey, setApiKey] = useState('');

  // storyÍ∞Ä ÏóÜÏúºÎ©¥ Î°úÎî© ÌëúÏãú
  if (!story) {
    return (
      <div className="api-screen">
        <div className="api-container">
          <div className="loading-text">Ïä§ÌÜ†Î¶¨Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</div>
        </div>
      </div>
    );
  }

  const handleStart = () => {
    if (!apiKey.trim()) {
      alert("API ÌÇ§Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî!");
      return;
    }
    onStart(apiKey);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleStart();
    }
  };

  // Î™®Îì† ÌÉúÍ∑∏ Ìï©ÏπòÍ∏∞
  const allTags = story.storyTags ? 
    [...(story.storyTags.genre || []), ...(story.storyTags.mood || []), ...(story.storyTags.situation || [])] 
    : [];

  return (
    <div className="story-detail-screen">
      {/* ÏÉÅÎã® Í≥†Ï†ï Ìó§Îçî */}
      <div className="fixed-header">
        <Link to="/" className="back-button">‚Üê Îí§Î°ú</Link>
        <div className="header-logo">
          <img src="/cat-icon.png" alt="KIND CAT" className="app-icon-small" />
          <span className="app-name-small">KIND CAT</span>
        </div>
      </div>

      {/* Ïä§ÌÜ†Î¶¨ ÏÉÅÏÑ∏ ÎÇ¥Ïö© */}
      <div className="story-detail-content">
        {/* Ïç∏ÎÑ§Ïùº */}
        <div className="story-detail-thumbnail">
          {story.thumbnail ? (
            <img src={story.thumbnail} alt={story.storyTitle} />
          ) : (
            <div className="thumbnail-placeholder-large">
              <span className="placeholder-icon-large">üìñ</span>
            </div>
          )}
        </div>

        {/* Ïä§ÌÜ†Î¶¨ Ï†úÎ™© */}
        <h1 className="story-detail-title">{story.storyTitle}</h1>

        {/* Ïä§ÌÜ†Î¶¨ ÏÑ§Î™Ö */}
        <p className="story-detail-description">{story.description}</p>

        {/* ÌÉúÍ∑∏ */}
        {allTags.length > 0 && (
          <div className="story-detail-tags">
            {allTags.map((tag, idx) => (
              <span key={idx} className="detail-tag">{tag}</span>
            ))}
          </div>
        )}

        {/* Ï∫êÎ¶≠ÌÑ∞ Ï†ïÎ≥¥ */}
        <div className="characters-info">
          <h2 className="section-title">Îì±Ïû•Ïù∏Î¨º</h2>

          {/* Ï∫êÎ¶≠ÌÑ∞ A */}
          <div className="character-card">
            <div className="character-header">
              <span className="character-role top">üî∫ Í≥µ</span>
              <h3 className="character-name">{story.characterA.name}</h3>
              <span className="character-age">{story.characterA.age}ÏÑ∏</span>
            </div>
            <div className="character-details">
              <p className="character-occupation">{story.characterA.occupation}</p>
              <p className="character-personality">{story.characterA.personality}</p>
              {story.characterA.tags && story.characterA.tags.length > 0 && (
                <div className="character-tags">
                  {story.characterA.tags.map((tag, idx) => (
                    <span key={idx} className="char-tag">{tag}</span>
                  ))}
                </div>
              )}
            </div>
          </div>

          {/* Ï∫êÎ¶≠ÌÑ∞ B */}
          <div className="character-card">
            <div className="character-header">
              <span className="character-role bottom">üîª Ïàò</span>
              <h3 className="character-name">{story.characterB.name}</h3>
              <span className="character-age">{story.characterB.age}ÏÑ∏</span>
            </div>
            <div className="character-details">
              <p className="character-occupation">{story.characterB.occupation}</p>
              <p className="character-personality">{story.characterB.personality}</p>
              {story.characterB.tags && story.characterB.tags.length > 0 && (
                <div className="character-tags">
                  {story.characterB.tags.map((tag, idx) => (
                    <span key={idx} className="char-tag">{tag}</span>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>

        {/* ÏãúÎÇòÎ¶¨Ïò§ Ï†ïÎ≥¥ */}
        <div className="scenario-info">
          <h2 className="section-title">ÏãúÎÇòÎ¶¨Ïò§</h2>
          <div className="scenario-details">
            <p><strong>Í¥ÄÍ≥Ñ:</strong> {story.scenario.relationship}</p>
            <p><strong>Ïû•ÏÜå:</strong> {story.scenario.location}</p>
            <p><strong>ÏãúÍ∞Ñ:</strong> {story.scenario.time}</p>
            <div className="scenario-situation">
              <strong>ÏÉÅÌô©:</strong>
              <p className="situation-text">{story.scenario.situation}</p>
            </div>
          </div>
        </div>

        {/* API ÌÇ§ ÏûÖÎ†• */}
        <div className="api-input-section">
          <h2 className="section-title">Ïä§ÌÜ†Î¶¨ ÏãúÏûëÌïòÍ∏∞</h2>
          <input
            type="password"
            className="api-input-field"
            placeholder="Gemini API ÌÇ§ (AIza...)"
            value={apiKey}
            onChange={(e) => setApiKey(e.target.value)}
            onKeyPress={handleKeyPress}
          />
          <button className="btn-start-story" onClick={handleStart}>
            Ïä§ÌÜ†Î¶¨ ÏãúÏûëÌïòÍ∏∞
          </button>
          <div className="api-help">
            <a 
              href="https://aistudio.google.com/app/apikey" 
              target="_blank" 
              rel="noopener noreferrer"
            >
              API ÌÇ§ Î∞úÍ∏âÎ∞õÍ∏∞
            </a>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ApiKeyScreen;
