          ...STORY_CONFIG,
          ...parsedConfig
        };
        setStoryConfig(mergedConfig);
        console.log('‚úÖ Ï†ÄÏû•Îêú ÏÑ§Ï†ï Î∂àÎü¨Ïò¥!');
