import React, { useState } from 'react';
import './AdminPage.css';

function AdminPage({ onSaveConfig }) {
  const [password, setPassword] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // ì•± ì„¤ì •
  const [appSettings, setAppSettings] = useState({
    icon: null,
    iconPreview: 'ðŸ±',
    fontFamily: 'Malgun Gothic'
  });

  // ìºë¦­í„° A (ê³µ)
  const [charA, setCharA] = useState({
    name: 'ê°•íƒœí˜„',
    age: '35',
    role: 'ê³µ',
    personality: 'ëƒ‰ì •í•˜ê³  ê³¼ë¬µí•˜ë©° ê°ì • í‘œí˜„ì´ ì ìŒ',
    occupation: 'ëŒ€ê¸°ì—… ì „ë¬´ì´ì‚¬',
    appearance: 'ë‚ ì¹´ë¡œìš´ ëˆˆë§¤, ì°¨ê°€ìš´ ì¸ìƒ',
    speech: 'ê°„ê²°í•˜ê³  ëª…ë ¹ì¡°',
    avatar: null,
    avatarPreview: 'https://via.placeholder.com/100/C9395A/FFFFFF?text=K',
    sexualDetails: {
      size: 'í¼',
      stamina: 'ê°•í•¨',
      style: 'ê±°ì¹ ê³  ì§€ë°°ì ',
      sensitivity: 'ë‚®ìŒ'
    },
    tags: ['#ëƒ‰í˜ˆê³µ', '#ìž¬ë²Œê³µ', '#ê°•ê³µ'],
    preferredActions: ['ëª© ìž¡ê¸°', 'ë²½ ë°€ì–´ë¶™ì´ê¸°', 'ëª…ë ¹ì¡° ëŒ€í™”'],
    avoidedActions: ['ì• êµ', 'ìˆ˜ë™ì  íƒœë„', 'ëˆˆë¬¼']
  });

  // ìºë¦­í„° B (ìˆ˜)
  const [charB, setCharB] = useState({
    name: 'ì´ì¤€ì„œ',
    age: '27',
    role: 'ìˆ˜',
    personality: 'ê²‰ìœ¼ë¡œëŠ” ê¹Œì¹ í•˜ì§€ë§Œ ì†ì€ ìˆœìˆ˜í•¨',
    occupation: 'ì‹ ìž… ì‚¬ì›',
    appearance: 'í° ëˆˆ, ë¶€ë“œëŸ¬ìš´ ì¸ìƒ',
    speech: 'ì¡´ëŒ“ë§ê³¼ ë°˜ë§ì„ ì„žì–´ ì”€',
    avatar: null,
    avatarPreview: 'https://via.placeholder.com/100/E8749B/FFFFFF?text=L',
    sexualDetails: {
      sensitivity: 'ë§¤ìš° ë†’ìŒ',
      bodyType: 'ë‚ ì”¬í•¨',
      reactions: 'ì†Œë¦¬ ë§Žì´ ëƒ„',
      flexibility: 'ë†’ìŒ'
    },
    tags: ['#ê¹Œì¹ ìˆ˜', '#ì¸¤ë°ë ˆ', '#ìˆœìˆ˜ìˆ˜'],
    preferredActions: ['ì €í•­í•˜ë‹¤ êµ´ë³µ', 'ìž…ìˆ  ê¹¨ë¬¼ê¸°', 'ìˆ˜ì¹˜ì‹¬ í‘œí˜„'],
    avoidedActions: ['ê³¼ë„í•œ ë³µì¢…', 'ë¬´í‘œì •', 'ê³µê²©ì„±']
  });

  // ì‹œë‚˜ë¦¬ì˜¤
  const [scenario, setScenario] = useState({
    title: 'ì˜¤í”¼ìŠ¤ ë¡œë§¨ìŠ¤',
    description: 'ëƒ‰í˜ˆí•œ ìƒì‚¬ì™€ ê¹Œì¹ í•œ ì‹ ìž… ì‚¬ì›ì˜ ìœ„í—˜í•œ ì‚¬ë‚´ì—°ì• ',
    relationship: 'ìƒì‚¬ì™€ ë¶€í•˜',
    location: 'íšŒì‚¬ ëŒ€í‘œì´ì‚¬ì‹¤',
    situation: 'ì•¼ê·¼ ì¤‘ ë‘˜ë§Œ ë‚¨ê²Œ ëœ ìƒí™©',
    time: 'ë°¤ 10ì‹œ'
  });

  // ì´ë¯¸ì§€
  const [images, setImages] = useState([
    { id: 1, threshold: 20, name: 'ì²« ë§Œë‚¨', file: null, preview: null },
    { id: 2, threshold: 40, name: 'ì¹œë°€í•´ì§', file: null, preview: null },
    { id: 3, threshold: 60, name: 'ì„¤ë ˆëŠ” ìˆœê°„', file: null, preview: null },
    { id: 4, threshold: 80, name: 'ê¹Šì–´ì§€ëŠ” ê´€ê³„', file: null, preview: null },
    { id: 5, threshold: 100, name: 'ì™„ì „í•œ ì‹ ë¢°', file: null, preview: null }
  ]);

  const AVAILABLE_FONTS = [
    'Malgun Gothic',
    'Noto Sans KR',
    'Nanum Gothic',
    'Nanum Myeongjo',
    'Black Han Sans',
    'Do Hyeon',
    'Jua',
    'Sunflower'
  ];

  const COMMON_TAGS_GONG = [
    '#ëƒ‰í˜ˆê³µ', '#ìž¬ë²Œê³µ', '#ê°•ê³µ', '#ê´‘ê³µ', '#ì§‘ì°©ê³µ',
    '#ì ˆë¥œê³µ', '#ëŠ¥ê¸€ê³µ', '#ë‹¤ì •ê³µ', '#í—Œì‹ ê³µ', '#ì—°ìƒê³µ'
  ];

  const COMMON_TAGS_SU = [
    '#ê¹Œì¹ ìˆ˜', '#ìˆœìˆ˜ìˆ˜', '#ë‹¤ì •ìˆ˜', '#í—Œì‹ ìˆ˜', '#ìœ í˜¹ìˆ˜',
    '#ìƒì²˜ìˆ˜', '#ìžë‚®ìˆ˜', '#ê³µì´ì—ˆìˆ˜', '#ì¸¤ë°ë ˆ', '#ì—°ìƒìˆ˜'
  ];

  const handleLogin = (e) => {
    e.preventDefault();
    if (password === 'admin1234') {
      setIsAuthenticated(true);
    } else {
      alert('ë¹„ë°€ë²ˆí˜¸ê°€ í‹€ë ¸ìŠµë‹ˆë‹¤!');
    }
  };

  const handleAppIconUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setAppSettings({
          ...appSettings,
          icon: file,
          iconPreview: e.target.result
        });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleAvatarUpload = (character, e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        if (character === 'A') {
          setCharA({ ...charA, avatar: file, avatarPreview: e.target.result });
        } else {
          setCharB({ ...charB, avatar: file, avatarPreview: e.target.result });
        }
      };
      reader.readAsDataURL(file);
    }
  };

  const handleImageUpload = (index, e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        const newImages = [...images];
        newImages[index].file = file;
        newImages[index].preview = e.target.result;
        setImages(newImages);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleTagToggle = (character, tag) => {
    if (character === 'A') {
      const tags = charA.tags.includes(tag)
        ? charA.tags.filter(t => t !== tag)
        : [...charA.tags, tag];
      setCharA({ ...charA, tags });
    } else {
      const tags = charB.tags.includes(tag)
        ? charB.tags.filter(t => t !== tag)
        : [...charB.tags, tag];
      setCharB({ ...charB, tags });
    }
  };

  const handleAddCustomTag = (character) => {
    const tag = prompt('ìƒˆ íƒœê·¸ ìž…ë ¥ (ì˜ˆ: #ì§‘ì°©ê³µ)');
    if (tag && tag.startsWith('#')) {
      if (character === 'A') {
        setCharA({ ...charA, tags: [...charA.tags, tag] });
      } else {
        setCharB({ ...charB, tags: [...charB.tags, tag] });
      }
    } else if (tag) {
      alert('íƒœê·¸ëŠ” #ìœ¼ë¡œ ì‹œìž‘í•´ì•¼ í•©ë‹ˆë‹¤!');
    }
  };

  const handleArrayInput = (character, field, index, value) => {
    if (character === 'A') {
      const newArray = [...charA[field]];
      if (value === '') {
