import React, { useState } from 'react';
import './AdminPage.css';

function AdminPage({ onSaveConfig }) {
  const [password, setPassword] = useState('');
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // 캐릭터 A (공)
  const [charA, setCharA] = useState({
    name: '강태현',
    age: '35',
    role: '공',
    personality: '냉정하고 과묵하며 감정 표현이 적음',
    occupation: '대기업 전무이사',
    appearance: '날카로운 눈매, 차가운 인상',
    speech: '간결하고 명령조',
    avatar: 'https://via.placeholder.com/100/C9395A/FFFFFF?text=K'
  });

  // 캐릭터 B (수)
  const [charB, setCharB] = useState({
    name: '이준서',
    age: '27',
    role: '수',
    personality: '겉으로는 까칠하지만 속은 순수함',
    occupation: '신입 사원',
    appearance: '큰 눈, 부드러운 인상',
    speech: '존댓말과 반말을 섞어 씀',
    avatar: 'https://via.placeholder.com/100/E8749B/FFFFFF?text=L'
  });

  // 시나리오
  const [scenario, setScenario] = useState({
    title: '오피스 로맨스',
    description: '냉혈한 상사와 까칠한 신입 사원의 위험한 사내연애',
    relationship: '상사와 부하',
    location: '회사 대표이사실',
    situation: '야근 중 둘만 남게 된 상황',
    time: '밤 10시'
  });

  // 이미지
  const [images, setImages] = useState([
    { id: 1, threshold: 20, name: '첫 만남', url: '' },
    { id: 2, threshold: 40, name: '친밀해짐', url: '' },
    { id: 3, threshold: 60, name: '설레는 순간', url: '' },
    { id: 4, threshold: 80, name: '깊어지는 관계', url: '' },
    { id: 5, threshold: 100, name: '완전한 신뢰', url: '' }
  ]);

  const handleLogin = (e) => {
    e.preventDefault();
    if (password === 'admin1234') {
      setIsAuthenticated(true);
    } else {
      alert('비밀번호가 틀렸습니다!');
    }
  };

  const handleCharAChange = (field, value) => {
    setCharA({ ...charA, [field]: value });
  };

  const handleCharBChange = (field, value) => {
    setCharB({ ...charB, [field]: value });
  };

  const handleScenarioChange = (field, value) => {
    setScenario({ ...scenario, [field]: value });
  };

  const handleImageChange = (index, value) => {
    const newImages = [...images];
    newImages[index].url = value;
    setImages(newImages);
  };

  const handleDownloadConfig = () => {
    const config = {
      title: scenario.title,
      description: scenario.description,
      characterA: charA,
      characterB: charB,
      scenario: {
        relationship: scenario.relationship,
        location: scenario.location,
        situation: scenario.situation,
        time: scenario.time
      },
      images: images
    };

    const configText = `export const STORY_CONFIG = ${JSON.stringify(config, null, 2)};

export const SYSTEM_PROMPT = \`당신은 한국 BL 인터랙티브 픽션의 AI입니다.

[핵심 규칙]
1. 응답은 반드시 JSON 형식
2. 지문과 대사 명확히 구분
3. 캐릭터 성격 유지
4. 호감도 점진적 증가

[응답 형식]
{
  "narration": "지문 (200-300자)",
  "dialogues": [
    {"character": "${charA.name}", "text": "대사"},
    {"character": "${charB.name}", "text": "대사"}
  ],
  "affection_change": -10~+15,
  "excitement_change": 0~+10,
  "choices": ["선택1", "선택2", "선택3", "선택4"]
}

[작문 규칙]
- 감정을 신체 반응으로
- 1인칭 시점
- 자연스러운 대사
- 성격에 맞는 신음

[호감도]
0-20: 타인, 21-40: 경계, 41-60: 관심, 61-80: 호감, 81-95: 애정, 96-100: 신뢰\`;`;

    const blob = new Blob([configText], { type: 'text/javascript' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'storyConfig.js';
    a.click();
    URL.revokeObjectURL(url);
  };

  if (!isAuthenticated) {
    return (
      <div className="admin-login">
        <div className="login-container">
          <div className="admin-logo">🔐</div>
          <h1 className="admin-title">관리자 로그인</h1>
          <form onSubmit={handleLogin}>
            <input
              type="password"
              className="login-input"
              placeholder="비밀번호 입력"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <button type="submit" className="login-btn">로그인</button>
          </form>
          <p className="login-hint">힌트: admin1234</p>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-page">
      <div className="admin-header">
        <h1>🎨 KIND CAT 관리자</h1>
        <button className="logout-btn" onClick={() => setIsAuthenticated(false)}>
          로그아웃
        </button>
      </div>

      <div className="admin-content">
        {/* 스토리 정보 */}
        <div className="admin-section">
          <h2 className="section-title">📖 스토리 정보</h2>
          <div className="form-grid">
            <div className="form-group">
              <label>제목</label>
              <input
                type="text"
                value={scenario.title}
                onChange={(e) => handleScenarioChange('title', e.target.value)}
              />
            </div>
            <div className="form-group full-width">
              <label>설명</label>
              <textarea
                value={scenario.description}
                onChange={(e) => handleScenarioChange('description', e.target.value)}
              />
            </div>
          </div>
        </div>

        {/* 캐릭터 A (공) */}
        <div className="admin-section">
          <h2 className="section-title">🔺 공(攻) 캐릭터</h2>
          <div className="form-grid">
            <div className="form-group">
              <label>이름 *</label>
              <input
                type="text"
                value={charA.name}
                onChange={(e) => handleCharAChange('name', e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>나이</label>
              <input
                type="text"
                value={charA.age}
                onChange={(e) => handleCharAChange('age', e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>직업</label>
              <input
                type="text"
                value={charA.occupation}
                onChange={(e) => handleCharAChange('occupation', e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>성격</label>
              <input
                type="text"
                value={charA.personality}
                onChange={(e) => handleCharAChange('personality', e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>외모</label>
              <textarea
                value={charA.appearance}
                onChange={(e) => handleCharAChange('appearance', e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>말투</label>
              <textarea
                value={charA.speech}
                onChange={(e) => handleCharAChange('speech', e.target.value)}
              />
            </div>
            <div className="form-group full-width">
              <label>프로필 사진 URL</label>
              <input
                type="text"
                value={charA.avatar}
                onChange={(e) => handleCharAChange('avatar', e.target.value)}
                placeholder="https://..."
              />
              {charA.avatar && (
                <img src={charA.avatar} alt="preview" className="avatar-preview" />
              )}
            </div>
          </div>
        </div>

        {/* 캐릭터 B (수) */}
        <div className="admin-section">
          <h2 className="section-title">🔻 수(受) 캐릭터</h2>
          <div className="form-grid">
            <div className="form-group">
              <label>이름 *</label>
              <input
                type="text"
                value={charB.name}
                onChange={(e) => handleCharBChange('name', e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>나이</label>
              <input
                type="text"
                value={charB.age}
                onChange={(e) => handleCharBChange('age', e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>직업</label>
              <input
                type="text"
                value={charB.occupation}
                onChange={(e) => handleCharBChange('occupation', e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>성격</label>
              <input
                type="text"
                value={charB.personality}
                onChange={(e) => handleCharBChange('personality', e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>외모</label>
              <textarea
                value={charB.appearance}
                onChange={(e) => handleCharBChange('appearance', e.target.value)}
              />
            </div>
            <div className="form-group">
              <label>말투</label>
              <textarea
                value={charB.speech}
                onChange={(e) => handleCharBChange('speech', e.target.value)}
              />
            </div>
            <div className="form-group full-width">
              <label>프로필 사진 URL</label>
              <input
                type="text"
                value={charB.avatar}
                onChange={(e) => handleCharBChange('avatar', e.target.value)}
                placeholder="https://..."
              />
              {charB.avatar && (
                <img src={charB.avatar} alt="preview" className="avatar-preview" />
              )}
            </div>
          </div>
        </div>

        {/* 시나리오 설정 */}
        <div className="admin-section">
          <h2 className="section-title">🎬 시나리오 설정</h2>
          <div className="form-grid">
            <div className="form-group">
              <label>두 사람의 관계</label>
              <input
                type="text"
                value={scenario.relationship}
                onChange={(e) => handleScenarioChange('relationship', e.target.value)}
                placeholder="예: 상사와 부하"
              />
            </div>
            <div className="form-group">
              <label>시작 시간</label>
              <input
                type="text"
                value={scenario.time}
                onChange={(e) => handleScenarioChange('time', e.target.value)}
                placeholder="예: 밤 10시"
              />
            </div>
            <div className="form-group">
              <label>첫 만남 장소</label>
              <input
                type="text"
                value={scenario.location}
                onChange={(e) => handleScenarioChange('location', e.target.value)}
                placeholder="예: 회사 대표이사실"
              />
            </div>
            <div className="form-group">
              <label>첫 만남 상황</label>
              <textarea
                value={scenario.situation}
                onChange={(e) => handleScenarioChange('situation', e.target.value)}
                placeholder="예: 야근 중 둘만 남게 된 상황"
              />
            </div>
          </div>
        </div>

        {/* 이미지 설정 */}
        <div className="admin-section">
          <h2 className="section-title">🎨 배경 이미지 (호감도별)</h2>
          <div className="image-list">
            {images.map((img, index) => (
              <div key={img.id} className="image-item">
                <div className="image-info">
                  <strong>{img.name}</strong>
                  <span className="threshold-badge">{img.threshold}점</span>
                </div>
                <input
                  type="text"
                  value={img.url}
                  onChange={(e) => handleImageChange(index, e.target.value)}
                  placeholder="이미지 URL (https://...)"
                />
                {img.url && (
                  <img src={img.url} alt={img.name} className="image-preview" />
                )}
              </div>
            ))}
          </div>
        </div>

        {/* 저장 버튼 */}
        <div className="admin-actions">
          <button className="btn-download" onClick={handleDownloadConfig}>
            📥 storyConfig.js 다운로드
          </button>
          <div className="instruction">
            다운로드한 파일을 <code>src/config/storyConfig.js</code>에 덮어쓰기하세요!
          </div>
        </div>
      </div>
    </div>
  );
}

export default AdminPage;
