import React, { useState } from 'react';
import './ApiKeyScreen.css';

function ApiKeyScreen({ story, onStart }) {
  const [apiKey, setApiKey] = useState('');

  // storyÍ∞Ä ÏóÜÏúºÎ©¥ Î°úÎî© ÌëúÏãú
  if (!story) {
    return (
      <div className="api-screen">
        <div className="api-container">
          <div className="api-logo">üê±</div>
          <h1 className="api-title">KIND CAT</h1>
          <p className="api-subtitle">Ïä§ÌÜ†Î¶¨Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</p>
        </div>
      </div>
    );
  }

  const handleStart = () => {
    if (!apiKey.trim()) {
      alert("API ÌÇ§Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî!");
      return;
    }
    onStart(apiKey);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleStart();
    }
  };

  return (
    <div className="api-screen">
      <div className="api-container">
        <div className="api-logo">üê±</div>
        <h1 className="api-title">KIND CAT</h1>
        <p className="api-subtitle">{story.title}</p>

        <div className="story-description">
          <strong>üìñ Ïä§ÌÜ†Î¶¨ ÏÜåÍ∞ú</strong><br/><br/>
          {story.description}<br/><br/>
          <strong>Îì±Ïû•Ïù∏Î¨º</strong><br/>
          üî∫ {story.characterA.name} ({story.characterA.age}ÏÑ∏) - {story.characterA.occupation}<br/>
          üîª {story.characterB.name} ({story.characterB.age}ÏÑ∏) - {story.characterB.occupation}
        </div>

        <input
          type="password"
          className="input-field"
          placeholder="Gemini API ÌÇ§ (AIza...)"
          value={apiKey}
          onChange={(e) => setApiKey(e.target.value)}
          onKeyPress={handleKeyPress}
        />

        <button className="btn-primary" onClick={handleStart}>
          Ïä§ÌÜ†Î¶¨ ÏãúÏûëÌïòÍ∏∞
